->Proyecto,10);		$this->Concepto_flujo_caja=$this->prepara_cadena($this->Concepto_flujo_caja,8);		$this->Numero_doc_conciliacion=$this->prepara_valor($this->Numero_doc_conciliacion,6,0);		$this->Numero_doc_cruce=$this->prepara_valor($this->Numero_doc_cruce,6,0);		$this->Prefijo_doc_proveedor=$this->prepara_cadena($this->Prefijo_doc_proveedor,4);		$this->Numero_doc_proveedor=$this->prepara_cadena($this->Numero_doc_proveedor,12);		$this->Vendedor=$this->prepara_cadena($this->Vendedor,13);		$this->Valor_descuento_pronto_pago=$this->prepara_valor($this->Valor_descuento_pronto_pago,11,2).'+';		$this->Cod_banco_cheque=$this->prepara_cadena($this->Cod_banco_cheque,4);		$this->Numero_cheque=$this->prepara_valor($this->Numero_cheque,6,0);		$this->Referencia_consignacion=$this->prepara_cadena($this->Referencia_consignacion,10);		$this->Numero_cuenta=$this->prepara_cadena($this->Numero_cuenta,20);		$this->Detalle1_doc=$this->prepara_cadena($this->Detalle1_doc,60);		$this->Detalle2_doc=$this->prepara_cadena($this->Detalle2_doc,60);		//-----------------------------------------------------------------------------------------------------------------------		$Resultado=$this->Consecutivo_grabacion.$this->Empresa.$this->Centro_operacion.$this->Tipo_documento;		$Resultado.=$this->Numero_documento.$this->Fecha_documento.$this->Tercero.$this->Sucursal_tercero;		$Resultado.=$this->Indicador_anulacion.$this->Valor_documento.$this->Cuenta_contable.$this->Centro_operacion_mov.$this->Tercero_mov.$this->Sucursal_tercero_mov;		$Resultado.=$this->Detalle1.$this->Detalle2.$this->DC.$this->Valor.$this->Tasa_imp_retencion.$this->Tasa_base_iva;		$Resultado.=$this->Base_iva_retencion.$this->Valor_me.$this->Tasa_cambio.$this->Tasa_conversion.$this->Centro_costos.$this->Proyecto;		$Resultado.=$this->Concepto_flujo_caja.$this->Tipo_doc_conciliacion.$this->Numero_doc_conciliacion.$this->Tipo_doc_cruce.$this->Numero_doc_cruce.$this->Cuota_cruce;		$Resultado.=$this->Fecha_vencimiento_