<?php/** * Clase para interfase de documentos HELISA * * @version $Id$ * @copyright 2009 ARTURO QUINTERO RODRIGUEZ  administracion@intercolombia.net */class Interfase_documento{	var $Tipo_comprobante='CC';	var $Numero_documento='0';	var $Fecha_documento='00/00/0000';  // dd/mm/aaaa	var $Cuenta_contable='00000000';  // 8 posiciones	var $Descripcion='                                                  '; // 50 espacios	var $Centro_costos='0000';	var $Valor='000000000000000'; // 13 enteros 2 decimales	var $Afectacion='D'; // D | C	var $Nit='0000000000000';	var $Cheque='0';	var $Fin_de_linea="\r\n";	function genera()	{		$this->Descripcion=$this->prepara_cadena($this->Descripcion,50);		$this->Valor=$this->prepara_valor($this->Valor,13,2);		$this->Numero_documento=$this->prepara_valor($this->Numero_documento,8,0);		//-----------------------------------------------------------------------------------------------------------------------		$Resultado=$this->Tipo_comprobante.','.$this->Numero_documento.','.$this->Fecha_documento;		$Resultado.=','.$this->Cuenta_contable.','.$this->Descripcion.','.$this->Centro_costos;		$Resultado.=','.$this->Valor.','.$this->Afectacion.','.$this->Nit.','.$this->Cheque;		$Resultado.=$this->Fin_de_linea;		return $Resultado;	}	function prepara_valor($Valor=0,$Enteros=13,$Decimales=0)	{		$Entero=intval($Valor);		$Decimal=round($Valor-$Entero,$Decimales);		$Cadena1=strval($Entero);		$Resultado=str_pad($Cadena1,$Enteros,'0',STR_PAD_LEFT);		if($Decimales)		{			$Cadena2=strval($Decimal);			$Resultado.='.'.str_pad(substr($Cadena2,strpos($Cadena2,'.')+1,$Decimales),$Decimales,'0',STR_PAD_RIGHT);		}		return $Resultado;	}	function prepara_cadena($Cadena,$Longitud=50)	{		return str_pad(substr($Cadena,0,$Longitud),$Longitud," ",STR_PAD_RIGHT);	}}class interfase_tercero{	var $Identidad=0; 	var $Dv=' ';	var $Clase='A';   //  A = NIT   C= CEDULA	var $Nombres='';  	var $Direccion='';	var $Telefono='';	var $Fax='';	var $Ciudad='1101'; // Bogota	var $Nombre_ciudad='BOGOTA';	var $Apartado='';	var $Email='';	var $Apellido1='';	var $Apellido2='';	var $Nombre1='';	var $Nombre2='';	var $Fin_de_linea="\r\n";		function genera()	{		$Resultado=$this->Identidad.','.$this->Dv.','.$this->Clase;		$Resultado.=','.$this->Nombres.','.$this->Direccion.','.$this->Telefono;		$Resultado.=','.$this->Fax.','.$this->Ciudad.','.$this->Nombre_ciudad.','.$this->Apartado;		$Resultado.=','.$this->Email.','.$this->Apellido1.','.$this->Apellido2.','.$this->Nombre1.','.$this->Nombre2;		$Resultado.=$this->Fin_de_linea;		return $Resultado;	}}class interfase_cliente{	var $Codigo=1;   // tipo de in	var $Identidad=0; 	var $Dv=' ';	var $Clase='A';   //  A = NIT   C= CEDULA	var $Nombres='';  	var $Local='';var $Representante='';var $Encargado_pagos='';var $Encargado_compras='';	var $Direccion='';var $Direccion_entrega='';var $Telefono='';var $Fax='';var $Ciudad='1101'; // Bogota	var $Nombre_ciudad='BOGOTA';var $Apartado='';var $Email='';	var $Zona='';var $Grupo='';var $Campo_libre1='';var $Campo_libre2='';var $Tolerancia='';var $Cupo='';	var $Cliente_desde='';var $Dias_pago='';var $Observaciones='';var $Regimen='';var $Area_ica='';	var $Forma_pago='';var $Plan_descuento='';var $Descuento='';var $Lista_precios_articulos='';var $Lista_precios_servicios='';	var $Saldo_inicial='0';var $Debito_enero='0';var $Debito_febrero='0';var $Debito_marzo='0';var $Debito_abril='0';var $Debito_mayo='0';var $Debito_junio='0';	var $Debito_julio='0';var $Debito_agosto='0';var $Debito_septiembre='0';var $Debito_octubre='0';var $Debito_noviembre='0';var $Debito_diciembre='0';	var $Debito_ajustes='0';var $Debito_cancelacion='0';	var $Credito_enero='0';var $Credito_febrero='0';var $Credito_marzo='0';var $Credito_abril='0';var $Credito_mayo='0';var $Credito_junio='0';	var $Credito_julio='0';var $Credito_agosto='0';var $Credito_septiembre='0';var $Credito_octubre='0';var $Credito_noviembre='0';var $Credito_diciembre='0';	var $Credito_ajustes='0';var $Credito_cancelacion='0';	var $Codigo_vendedor='0';var $Cuenta_cartera='';var $Cartera='CLIENTES';	var $Apellido1='';var $Apellido2='';var $Nombre1='';var $Nombre2='';		var $Fin_de_linea="\r\n";		function genera()	{		$Resultado=$this->Codigo.','.$this->Identidad.','.$this->Dv.','.$this->Clase.','.$this->Nombres.','.$this->Local.','.$this->Representante.','.$this->Encargado_pagos.','.$this->Encargado_compras;		$Resultado.=','.$this->Direccion.','.$this->Direccion_entrega.','.$this->Telefono.','.$this->Fax.','.$this->Ciudad.','.$this->Nombre_ciudad.','.$this->Apartado;		$Resultado.=','.$this->Email.','.$this->Zona.','.$this->Grupo.','.$this->Campo_libre1.','.$this->Campo_libre2.','.$this->Tolerancia.','.$this->Cupo;		$Resultado.=','.$this->Cliente_desde.','.$this->Dias_pago.','.$this->Observaciones.','.$this->Regimen.','.$this->Area_ica;		$Resultado.=','.$this->Forma_pago.','.$this->Plan_descuento.','.$this->Descuento.','.$this->Lista_precios_articulos.','.$this->Lista_precios_servicios;		$Resultado.=','.$this->Saldo_inicial.','.$this->Debito_enero.','.$this->Debito_febrero.','.$this->Debito_marzo.','.$this->Debito_abril.','.$this->Debito_mayo.','.$this->Debito_junio;		$Resultado.=','.$this->Debito_julio.','.$this->Debito_agosto.','.$this->Debito_septiembre.','.$this->Debito_octubre.','.$this->Debito_noviembre.','.$this->Debito_diciembre;		$Resultado.=','.$this->Debito_ajustes.','.$this->Debito_cancelacion.','.$this->Credito_enero.','.$this->Credito_febrero.','.$this->Credito_marzo.','.$this->Credito_abril;		$Resultado.=','.$this->Credito_mayo.','.$this->Credito_junio.','.$this->Credito_julio.','.$this->Credito_agosto.','.$this->Credito_septiembre.','.$this->Credito_octubre;		$Resultado.=','.$this->Credito_noviembre.','.$this->Credito_diciembre.','.$this->Credito_ajustes.','.$this->Credito_cancelacion;		$Resultado.=','.$this->Codigo_vendedor.','.$this->Cuenta_cartera.','.$this->Cartera.','.$this->Apellido1.','.$this->Apellido2.','.$this->Nombre1.','.$this->Nombre2;		$Resultado.=$this->Fin_de_linea;		return $Resultado;	}}?>